mapper.update:
UPDATE XXL_RPC_REGISTRY_DATA AS t
SET
	t.`updateTime` = now()
WHERE t.`biz` = #{xxlRpcRegistryData.biz}
	and t.`env` = #{xxlRpcRegistryData.env}
	and t.`key` = #{xxlRpcRegistryData.key}
	and t.`value` = #{xxlRpcRegistryData.value}

mapper.select:
SELECT 
FROM XXL_RPC_REGISTRY_DATA AS t
where t.biz = #{biz}
	and t.env = #{env}
	and t.key = #{key}
ORDER BY t.value ASC

mapper.delete:
DELETE FROM XXL_RPC_REGISTRY_DATA
WHERE `biz` = #{biz}
	and `env` = #{env}
	and `key` = #{key}

DELETE FROM XXL_RPC_REGISTRY_DATA
WHERE `biz` = #{biz}
	and `env` = #{env}
	and `key` = #{key}
	and `value` = #{value}
